  exportMessages: function() {
    this.fileSuggestedName = 'socket-messages';
    this.exportContent = this.messages;
    this.exportMime = 'json';
    this.exportData();
  },

  _restoredHandler: function() {
    if (this.lastSocketUrl) {
      this.$.view.url = this.lastSocketUrl;
    }
  }
